function hasUniqueWords($string) {\n    // Split the string into words using spaces as delimiters\n    $words = preg_split('/\s+/', trim($string));\n\n    // Create an associative array to track word occurrences\n    $wordCount = [];\n\n    foreach ($words as $word) {\n        if (isset($wordCount[$word])) {\n            // If the word is already in the array, it's a duplicate\n            return false;\n        }\n        // Mark this word as seen\n        $wordCount[$word] = 1;\n    }\n\n    // If no duplicates were found, all words are unique\n    return true;\n}\n\n// Test cases\necho hasUniqueWords("google makes love") ? 'Acceptable' : 'Not Acceptable'; // Output: Acceptable\necho "\n";\necho hasUniqueWords("google makes google love") ? 'Acceptable' : 'Not Acceptable'; // Output: Not Acceptable\necho "\n";\necho hasUniqueWords("google makes love love google") ? 'Acceptable' : 'Not Acceptable'; // Output: Not Acceptable