function callerInfo() {\n    $caller = [];\n    // Get the previous caller from stack trace if available\n    if (isset(debug_backtrace(DEBUG_BACKTRACE_IGNORE_ARGS, 2)[1])) {\n        $call = debug_backtrace(DEBUG_BACKTRACE_IGNORE_ARGS, 2)[1];\n        $caller['class'] = isset($call['class']) ? $call['class'] : null;\n        $caller['method'] = $call['function'];\n    }\n    return $caller;\n}\n\n// Usage\n$callingMethod = callerInfo();\necho "Class: {$callingMethod['class']} Method: {$callingMethod['method']}";