$database = Database::load();\n$sql = "SELECT * FROM test_multipe_refcursor();";\n$stmt = $database->query($sql);\n\n$results = [];\nwhile ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {\n    // Fetch each result set descriptor\n    $resultName = array_values($row)[0]; \n\n    // Now, fetch the actual data for this result set\n    $innerStmt = $database->query("FETCH ALL IN \"$resultName\"");\n    $results[] = $innerStmt->fetchAll(PDO::FETCH_ASSOC);\n}\n\n// Close your cursors if necessary (optional)\n$closeStmt = $database->exec('CLOSE ALL');\n\n// Access results like so:\nforeach ($results as $index => $result) {\n    print_r($result);\n}