<?php\n// Initialize database connection (assuming you have already established it)\n$db_con = new mysqli('host', 'user', 'password', 'database');\n\n// Check connection\nif ($db_con->connect_error) {\n    die("Connection failed: " . $db_con->connect_error);\n}\n\n// Run the query with SQL_CALC_FOUND_ROWS\n$result = $db_con->query("SELECT SQL_CALC_FOUND_ROWS * FROM users LIMIT 0,10");\n\n// Immediately after, run the FOUND_ROWS() query\n$count_result = $db_con->query("SELECT FOUND_ROWS() as totalcount");\n\n// Fetch the result\nif ($row = $count_result->fetch_assoc()) {\n    $total = $row['totalcount'];\n} else {\n    $total = 0; // Fallback or error handling\n}\n\necho "Total count: " . $total;\n\n// Close connections\n$result->free();\n$count_result->free();\n$db_con->close();\n?>