<?php\n/*\nPlugin Name: Custom RSS Feed\nDescription: A simple plugin to generate a custom RSS feed.\n*/\n\nfunction my_custom_rss_feed() {\n    // Check if the custom feed is requested\n    if (get_query_var('feed') === 'custom') {\n        header('Content-Type: application/rss+xml; charset=UTF-8');\n        \n        echo '<?xml version="1.0" encoding="UTF-8"?><rss version="2.0">';\n        echo '<channel>';\n        echo '<title>My Custom RSS Feed</title>';\n        echo '<link>' . home_url() . '</link>';\n        echo '<description>This is a custom RSS feed</description>';\n\n        // Fetch the latest posts\n        $args = array('posts_per_page' => 10);\n        $query = new WP_Query($args);\n\n        while ($query->have_posts()) {\n            $query->the_post();\n            echo '<item>';\n            echo '<title>' . get_the_title() . '</title>';\n            echo '<link>' . get_permalink() . '</link>';\n            echo '<pubDate>' . get_the_date('r') . '</pubDate>';\n            echo '<description><![CDATA[' . get_the_excerpt() . ']]></description>';\n            echo '</item>';\n        }\n\n        wp_reset_postdata();\n        \n        echo '</channel>';\n        echo '</rss>';\n        exit;\n    }\n}\nadd_action('template_redirect', 'my_custom_rss_feed');