function ensureHttp($url) {\n    // Trim any whitespace from the beginning and end of the URL\n    $url = trim($url);\n\n    // Check if the URL does not start with http:// or https://\n    if (strpos($url, 'http://') !== 0 && strpos($url, 'https://') !== 0) {\n        $url = 'http://' . $url;\n    }\n\n    return $url;\n}\n\n// Example usage:\n$userInputUrl = "www.example.com";\n$safeUrl = ensureHttp($userInputUrl);\necho $safeUrl; // Outputs: http://www.example.com