<?php\n\n// Load the HTML into a DOMDocument object\n$dom = new DOMDocument();\nlibxml_use_internal_errors(true); // Suppress warnings for invalid HTML\n$dom->loadHTML($html);\nlibxml_clear_errors();\n\n// Create an XPath object\n$xpath = new DOMXPath($dom);\n\n// Query all div elements with class "info"\nforeach ($xpath->query('//div[@class="info"]') as $node) {\n    // Use saveHTML to get the HTML of the node including tags\n    $htmlContent = $dom->saveHTML($node);\n    echo $htmlContent;\n}\n?>