mysqli_begin_transaction($connection);\n\nforeach ($myArray as $arrayFruit) {\n    // Prepare statements to safely execute queries and prevent SQL injection\n    $stmt = mysqli_prepare($connection, "UPDATE `Fruits` SET `Quantity` = ? WHERE `FruitName` = ?");\n    mysqli_stmt_bind_param($stmt, 'is', $newQuantity, $arrayFruit);\n    mysqli_stmt_execute($stmt);\n}\n\nmysqli_commit($connection); // Execute all queries as a single transactio