class MyClass {\n    private $myVariable;\n\n    // Constructor to accept a parameter and set it to a property\n    public function __construct($value) {\n        $this->myVariable = $value;\n    }\n\n    // Example method using the passed variable\n    public function displayValue() {\n        echo "The value is: " . htmlspecialchars($this->myVariable);\n    }\n}\n\n// Usage\n$instance = new MyClass("Hello, World!");\n$instance->displayValue();