// Sanitize redirect URL\n   function sanitize_url($url) {\n       $parsedUrl = parse_url($url);\n       if (!empty($parsedUrl['scheme']) && !in_array($parsedUrl['scheme'], ['http', 'https'])) {\n           return null; // Invalid scheme, do not redirect.\n       }\n       return $url;\n   }\n\n   // Use the sanitized URL for redirection\n   $redirect = isset($_GET['redir']) ? sanitize_url($_GET['redir']) : 'default.php';\n   header("Location: " . $redirect);\n   exit();