<?php\n// Disable error reporting for production; use error logging instead.\nerror_reporting(0);\n\n// Start a session to store user data if needed later.\nsession_start();\n\n// Retrieve POST data\n$id = $_POST['id'] ?? '';\n$pass = $_POST['pass'] ?? '';\n\nif (empty($id) || empty($pass)) {\n    die("ID and password are required.");\n}\n\n// Open the CSV file\n$handle = fopen("id.csv", "r");\nif (!$handle) {\n    die("Error opening the CSV file.");\n}\n\n$found = false;\nwhile (($data = fgetcsv($handle)) !== FALSE) {\n    // Compare submitted ID and pass with those from CSV\n    if ($id === $data[0] && $pass === $data[1]) {\n        $found = true;\n        break;  // Exit loop once a match is found\n    }\n}\n\nfclose($handle);\n\nif ($found) {\n    $_SESSION['user_id'] = $id;  // Store session variable for logged-in user\n    echo "Login successful!";\n} else {\n    echo "Invalid ID or password.";\n}\n?>