<?php\n// Check if 'q' parameter exists\nif(isset($_GET['q'])) {\n    $query = $_GET['q'];\n    echo "Searching for: " . htmlspecialchars($query); // Use htmlspecialchars to avoid XSS attacks\n} else {\n    echo "No search term provided.";\n}\n\n// Check if 'page' parameter exists\nif(isset($_GET['page'])) {\n    $page = intval($_GET['page']); // Convert to integer to prevent SQL injection\n    echo "Page number: " . $page;\n} else {\n    echo "Defaulting to page 1.";\n}\n?>