<?php\n$conn = mysqli_connect('localhost', 'mysql_user', 'mysql_password', 'norwegianfashion');\n\nif (!$conn) {\n    die("Connection failed: " . mysqli_connect_error());\n}\n\n// Set the charset to utf8\nif (!mysqli_set_charset($conn, "utf8")) {\n    printf("Error loading character set utf8: %s\n", mysqli_error($conn));\n} else {\n    printf("Current character set: %s\n", mysqli_character_set_name($conn));\n}\n\n// Query with accent characters\n$query = "SELECT id FROM myTable WHERE name = 'Fióre`s måløye'";\n$result = mysqli_query($conn, $query);\n\nif (mysqli_num_rows($result) > 0) {\n    while ($row = mysqli_fetch_assoc($result)) {\n        echo "ID: " . $row['id'] . "<br>";\n    }\n} else {\n    echo "0 results";\n}\n\n// Close connection\nmysqli_close($conn);\n?>