$query = "SELECT * FROM routes ORDER BY id DESC LIMIT 8;";\n$result = mysqli_query($connection, $query); // Assuming $connection is your database connection\n\n// Assume these variables are already set from another part of the script.\n$compareEcon = 88; // Example value for econ\n$compareX = 10; // Example value for X\n$compareY = 5;  // Example value for Y\n$compareGalaxy = 3; // Example value for Galaxy\n$comparePlanet = 7; // Example value for planet\n$players = 23; // Example number of players\n\n// Function to calculate distance based on galaxy, x, y, and planet\nfunction calculateDistance($g1, $x1, $y1, $p1, $g2, $x2, $y2, $p2) {\n    if ($g1 !== $g2) {\n        return abs($g1 - $g2);\n    } elseif ($x1 !== $x2 || $y1 !== $y2) {\n        return sqrt(pow(($x1 - $x2), 2) + pow(($y1 - $y2), 2));\n    } else {\n        return abs($p1 - $p2);\n    }\n}\n\n// Function to calculate the new column value\nfunction calculateNewColumn($econ, $distance, $players) {\n    return sqrt($econ) * (1 + sqrt($distance)/75 + sqrt($players)/10);\n}\n\necho "<table><tr><th>ID</th><th>Name</th><th>Base</th><th>Econ</th><th>Location</th><th>X</th><th>Y</th><th>Galaxy</th><th>Planet</th><th>Comment</th><th>New Column</th></tr>";\n\nwhile ($row = mysqli_fetch_assoc($result)) {\n    $distance = calculateDistance($compareGalaxy, $compareX, $compareY, $comparePlanet, $row['galaxy'], $row['x'], $row['y'], $row['planet']);\n    $newColumnValue = calculateNewColumn($row['econ'], $distance, $players);\n    \n    echo "<tr>";\n    foreach ($row as $td) {\n        echo "<td>$td</td>";\n    }\n    echo "<td>" . number_format($newColumnValue, 2) . "</td>";\n    echo "</tr>";\n}\n\necho "</table>";\n\nmysqli_free_result($result);