if ((isset($_POST["MM_insert"])) && ($_POST["MM_insert"] == "details")) {\n    $Year = $_POST['Year'];\n    $Month = $_POST['Month'];\n    $Day = $_POST['Day'];\n    $Hour = $_POST['Hour'];\n    $Minute = $_POST['Minute'];\n\n    $DateTime = "$Year-$Month-$Day $Hour:$Minute:00";\n    $Date = date('Y-m-d H:i:s', strtotime($DateTime));\n\n    // Using PDO prepared statement\n    $stmt = $conn->prepare("INSERT INTO sessions (Location1, Location2, Date) VALUES (?, ?, ?)");\n    $stmt->execute([$_POST['Location1'], $_POST['Location2'], $Date]);\n}