$ddl = array(\n    'CREATE TABLE tableOne ...',\n    'CREATE TABLE tableTwo (...);',\n    "CREATE TABLE `tableThree` (id INT);",\n    "-- Comment\nCREATE TABLE tableFour (\n  id INT\n);"\n);\n\nforeach($ddl as $tableDef) {\n    // Regular expression to match CREATE TABLE followed by any whitespace, an optional backtick-quoted or double-quoted name, or a simple identifier\n    if (preg_match('/\bCREATE\s+TABLE\s+`?([\w]+)`?\s*\(/i', $tableDef, $matches)) {\n        $tableName = $matches[1];\n        echo $tableName . "\n";\n    } else {\n        echo "Could not parse table name from: $tableDef\n";\n    }\n}