<?PHP\nfunction executeQuery($sql) {\n    $result = mysql_query($sql);\n    if (!$result) { // Check if query failed\n        $errorMessage = "<span style='FONT-SIZE:11px; FONT-COLOR: #000000; font-family=tahoma;'><center>An Internal Error has Occured. The error has been recorded<br></center></span>";\n        \n        // Check if the user is authorized to see detailed error messages\n        if (isset($_SESSION['auto_id']) && $_SESSION['auto_id'] == 1) {\n            $errorMessage .= "<br>" . htmlspecialchars($sql) . "<br><br>" . htmlspecialchars(mysql_error());\n        }\n        \n        die($errorMessage);\n    }\n    return $result;\n}\n?>