require_once('htmlMimeMail5.class.php');\n\n$mail = new htmlMimeMail5();\n\n// Set the character set\n$mail->setCharset('UTF-8');\n\n// Set the content type headers with correct encoding\n$mail->setTextCharset('UTF-8');\n$mail->setHtmlCharset('UTF-8');\n\n// Add the plain text part, use quoted-printable for better handling of line breaks\n$plainText = file_get_contents('path_to_your_text_file.txt');\n$mail->setText($plainText, 'quoted-printable');\n\n// Add the HTML part, base64 is recommended for HTML content as it's binary safe\n$htmlContent = file_get_contents('path_to_your_html_file.html');\n$mail->setHtml($htmlContent, 'base64');\n\n// Set other headers and send email\n$mail->setSubject('Your Subject Here');\n$mail->addTo('recipient@example.com', 'Recipient Name');\n$mail->fromAdd('sender@example.com', 'Sender Name');\n$result = $mail->send(array(), true);\n\nif ($result) {\n    echo "Email sent successfully.";\n} else {\n    echo "Failed to send email.";\n}