<?php\nheader('Content-Type: image/png');\n$text = 'This is some text';\n$font = '/path/to/font.ttf'; // Ensure this path is correct on your server\n\n// Create Image resource with dimensions large enough to fit the expected output\n$imageWidth = 400;\n$imageHeight = 100;\n\n$image = imagecreatetruecolor($imageWidth, $imageHeight);\nif (!$image) {\n    die('Cannot Initialize new GD image stream');\n}\n\n$backgroundColor = imagecolorallocate($image, 255, 255, 255);\n$textColor = imagecolorallocate($image, 0, 0, 0);\n\n// Fill the background\nimagefilledrectangle($image, 0, 0, $imageWidth, $imageHeight, $backgroundColor);\n\n// Calculate text bounding box to adjust positioning and size if needed.\n$fontSize = 20;\n$angle = 0;\n\n// Get the size of the text in pixels (bounding box)\n$textBox = imagettfbbox($fontSize, $angle, $font, $text);\n$textWidth = abs($textBox[4] - $textBox[0]);\n$textHeight = abs($textBox[5] - $textBox[1]);\n\n// Calculate position to center the text horizontally and vertically\n$positionX = ($imageWidth / 2) - ($textWidth / 2);\n$positionY = ($imageHeight / 2) + ($textHeight / 2);\n\nimagettftext($image, $fontSize, $angle, $positionX, $positionY, $textColor, $font, $text);\n\n// Output the image as a PNG\nimagepng($image);\n\n// Free up memory\nimagedestroy($image);\n?>