$response = $client->call('branchzipdata', $param);\n\n// Check if the response is base64 encoded\n$content = base64_decode($response, true);\nif ($content === false) {\n    echo "Failed to decode base64 content.";\n    exit;\n}\n\n$filename = "output_zip.gz";\n$filepath = '/safe/path/to/save/' . basename($filename); // Ensure a safe path\n\n// Open file in binary write mode\nif (!$handle = fopen($filepath, 'wb')) {\n    echo "Cannot open file ($filepath)";\n    exit;\n}\n\n// Write the content to the file\nif (fwrite($handle, $content) === FALSE) {\n    echo "Cannot write to file ($filepath)";\n    fclose($handle);\n    exit;\n}\n\nfclose($handle);\n\n// Use PHP's gzdecode function instead of system call for better security and portability\n$decoded_content = gzdecode($content);\nif ($decoded_content === false) {\n    echo "Failed to decompress the gzip content.";\n    exit;\n}\n\n$output_filename = 'output_file';\n$output_filepath = '/safe/path/to/save/' . basename($output_filename); // Ensure a safe path\n\n// Write the decompressed content to a new file\nif (!$output_handle = fopen($output_filepath, 'wb')) {\n    echo "Cannot open output file ($output_filepath)";\n    exit;\n}\n\nif (fwrite($output_handle, $decoded_content) === FALSE) {\n    echo "Cannot write to output file ($output_filepath)";\n    fclose($output_handle);\n    exit;\n}\n\nfclose($output_handle);\n\necho "File has been successfully decompressed and saved as $output_filepath";