<?php\n// Create DateTime objects for the start and end times\n$startTime = new DateTime('2023-10-01 14:00:00');\n$endTime = new DateTime('2023-10-01 15:30:00');\n\n// Calculate the difference between the two dates\n$interval = $startTime->diff($endTime);\n\n// Get the total minutes from the interval\n$minutes = $interval->days * 24 * 60 + $interval->h * 60 + $interval->i;\n\necho "Time difference in minutes: " . $minutes;\n?>