function generateList($array) {\n    $html = '<ul>';\n    \n    foreach ($array as $element) {\n        if (is_array($element['value'])) {\n            $title = '';\n            $subItems = [];\n            \n            foreach ($element['value'] as $subElement) {\n                if ($subElement['tag'] === 'Title') {\n                    $title = htmlspecialchars($subElement['value']);\n                } else {\n                    $subItems[] = $subElement;\n                }\n            }\n            \n            $html .= '<li>' . $title;\n            if (!empty($subItems)) {\n                $html .= generateList($subItems);\n            }\n            $html .= '</li>';\n        }\n    }\n    \n    $html .= '</ul>';\n    return $html;\n}\n\n$tree = array(\n    0 => array(\n        'tag' => 'NavigationMode',\n        'value' => array(\n            0 => array(\n                'tag' => 'Title',\n                'value' => 'Introduction'\n            ),\n            1 => array(\n                'tag' => 'NavigationNode',\n                'value' => array(\n                    0 => array(\n                        'tag' => 'Title',\n                        'value' => 'Sub Intro'\n                    )\n                )\n            )\n        )\n    ),\n    1 => array(\n        'tag' => 'NavigationMode',\n        'value' => array(\n            0 => array(\n                'tag' => 'Title',\n                'value' => 'Module 1'\n            )\n        )\n    )\n);\n\necho generateList($tree);