function fireAndForget($url) {\n    $parsedUrl = parse_url($url);\n    $host = $parsedUrl['host'];\n    $port = isset($parsedUrl['port']) ? $parsedUrl['port'] : 80;\n    $path = isset($parsedUrl['path']) ? $parsedUrl['path'] : '/';\n    $query = isset($parsedUrl['query']) ? '?' . $parsedUrl['query'] : '';\n\n    // Open a socket connection\n    $fp = fsockopen($host, $port, $errno, $errstr, 5);\n    if (!$fp) {\n        return false;\n    }\n\n    // Send the request headers and ignore the response by not reading from the socket\n    fwrite($fp, "GET {$path}{$query} HTTP/1.1\r\n");\n    fwrite($fp, "Host: {$host}\r\n");\n    fwrite($fp, "Connection: Close\r\n\r\n");\n\n    // Immediately close the connection without waiting for a response\n    fclose($fp);\n\n    return true;\n}\n\n// Example usage:\n$trackingUrl = 'http://internal-IP/userTracker.php?Name=UserName&Page=index.php';\nfireAndForget($trackingUrl);