class A {\n    public function getMyValue() {\n        // This should be overridden in child classes\n        return null;\n    }\n}\n\nclass B extends A {\n    private $myValue = 5;\n\n    public function getMyValue() {\n        return $this->myValue;\n    }\n}\n\nclass C extends A {\n    private $myValue = 6;\n\n    public function getMyValue() {\n        return $this->myValue;\n    }\n}\n\n$b = new B();\n$c = new C();\n\nvar_dump($b->getMyValue()); // Outputs: int(5)\nvar_dump($c->getMyValue()); // Outputs: int(6)