function convertToFloats($input) {\n    if (is_array($input)) {\n        return array_map('convertToFloats', $input);\n    } elseif (is_numeric($input)) {\n        return floatval($input);\n    }\n    return $input;\n}\n\n// Assuming $array is your fetched data from the database\n$array = convertToFloats($array);\n\n// Now $array should have all numeric values converted to floats.