<?php\n\n// URL of the page you want to scrape\n$url = 'http://example.com';\n\n// Initialize cURL session\n$ch = curl_init($url);\n\n// Set options for cURL transfer\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true); // Return the transfer as a string\ncurl_setopt($ch, CURLOPT_FOLLOWLOCATION, true); // Follow redirects if needed\n\n// Execute the request and store the response in $htmlContent\n$htmlContent = curl_exec($ch);\n\n// Check for errors\nif(curl_errno($ch)) {\n    echo 'Error:' . curl_error($ch);\n}\n\n// Close cURL session\ncurl_close($ch);\n\n// Load HTML content into DOMDocument\n$dom = new DOMDocument();\n@$dom->loadHTML($htmlContent); // Use @ to suppress warnings from invalid HTML\n\n// Get all elements with class "x"\n$classXElements = $dom->getElementsByClassName('x');\n\n$results = [];\n\n// Loop through each element and do something with its text content\nforeach ($classXElements as $element) {\n    $results[] = trim($element->nodeValue);\n}\n\n// Output results in a pretty array format\nprint_r($results);\n\n// Or output as CSV\necho implode(',', $results) . PHP_EOL;\n\n// Or convert to XML format\n$xml = new SimpleXMLElement('<root/>');\nforeach ($results as $result) {\n    $xml->addChild('item', htmlspecialchars($result));\n}\necho $xml->asXML();\n\n?>