function removeUrls($text) {\n    // Regular expression to match URLs with http/https or starting with www\n    return preg_replace('/\b(?:https?:\/\/|www\.)\S+/', '', $text);\n}\n\n$url1 = "Here is a funny site http://www.tunyurl.com/34934";\n$url2 = "Here is another funny site www.tinyurl.com/55555";\n\necho removeUrls($url1) . "\n"; // Output: Here is a funny site \necho removeUrls($url2) . "\n"; // Output: Here is another funny site