<?php\n// Example function to sanitize log entry before displaying it\nfunction sanitizeLogEntry($logEntry) {\n    // Use htmlspecialchars to convert special characters to HTML entities\n    return htmlspecialchars($logEntry, ENT_QUOTES, 'UTF-8');\n}\n\n// Example usage\n$logEntry = '<script>alert("Malicious script");</script> 192.168.1.1 - - [10/Oct/2023:13:55:36 +0200] "GET /example HTTP/1.1" 200 2326';\n$sanitizedLogEntry = sanitizeLogEntry($logEntry);\n\necho $sanitizedLogEntry;\n?>