function index()\n{\n    $this->load->library('carabiner');\n    \n    // Example data array to pass to the view\n    $data = [\n        'title' => 'Home Page',\n        'content_view' => 'include/home'\n    ];\n\n    // Load a master template that includes header, footer and other common parts\n    $this->load->view('template', $data);\n}\n\nfunction create()\n{\n    $this->load->library('carabiner');\n    \n    // Example data array to pass to the view\n    $data = [\n        'title' => 'Create Page',\n        'content_view' => 'page'\n    ];\n\n    // Use a single template for consistency\n    $this->load->view('template', $data);\n}