// Convert ISO-8859-1 string to UTF-8 before adding to the index\n$area = mb_convert_encoding(strtolower($item['area']), 'UTF-8', 'ISO-8859-1');\n\nif (mb_check_encoding($area, 'UTF-8')) {\n    $doc->addField(Zend_Search_Lucene_Field::keyword('_area', $area));\n} else {\n    // Handling error when conversion or check fails\n    error_log("Encoding conversion failed for area: " . $item['area']);\n}\n\n// Set analyzer before creating/using the index\nZend_Search_Lucene_Analysis_Analyzer::setDefault(new Zend_Search_Lucene_Analysis_Analyzer_Common_TextNum_CaseInsensitive());\n\n// Creating index from scratch (if necessary) with UTF-8 consideration\n$index = Zend_Search_Lucene::create('/path/to/indexdir');