$current_year = date('Y');\n$month_names = array(1 => 'January', 2 => 'February', 3 => 'March', 4 => 'April', 5 => 'May', 6 => 'June', \n                     7 => 'July', 8 => 'August', 9 => 'September', 10 => 'October', 11 => 'November', 12 => 'December');\n\n// Start with the current year and add months up to the current month\n$current_month = date('n');\n$years[$current_year] = array();\n\nfor ($month = 1; $month <= $current_month; $month++) {\n    $years[$current_year][] = $month_names[$month];\n}\n\n// Add all previous years with full 12 months\nfor ($year = $current_year - 1; $year >= 2008; $year--) {\n    $years[$year] = array_values($month_names);\n}