<?php\n// Check if request is POST and includes the correct token for security\nif ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['token']) && $_POST['token'] === 'your-unique-token') {\n    $file = 'filelist.txt';\n\n    // Attempt to open the file in write mode which clears its contents\n    if (fopen($file, 'w')) {\n        // Redirect back with success message\n        header('Location: index.html?message=File+cleared+successfully');\n        exit;\n    } else {\n        // Redirect with error message\n        header('Location: index.html?error=Failed+to+clear+file');\n        exit;\n    }\n} else {\n    // If not a POST request or token is invalid, redirect with error\n    header('Location: index.html?error=Unauthorized');\n    exit;\n}\n?>