function getArrayValueByString($array, $string) {\n    // Remove any spaces and quotes for consistency\n    $keys = str_replace(array(' ', '"', '[', ']'), '', $string);\n    \n    // Split by '][' to separate keys\n    $keysArray = explode('][', $keys);\n\n    // Traverse the array according to the keys\n    foreach ($keysArray as $key) {\n        if (isset($array[$key])) {\n            $array = $array[$key];\n        } else {\n            return null; // Return null if any key is not found\n        }\n    }\n\n    return $array;\n}\n\n// Example usage:\n$data = [\n    "status" => "success",\n    "response" => ["url" => "http://example.com"],\n    "entry" => [[ "text" => "Hello, world!" ]]\n];\n\necho getArrayValueByString($data, 'data["status"]'); // Outputs: success\necho getArrayValueByString($data, 'data["response"]["url"]'); // Outputs: http://example.com\necho getArrayValueByString($data, 'data["entry"]["0"]["text"]'); // Outputs: Hello, world!