<?php\n$ftp_server = "ftp.example.com";\n$ftp_user_name = "username";\n$ftp_user_pass = "password";\n$remote_file = "/path/to/your/file/Final.pdf"; // Path on the FTP server\n\n// Set up basic connection\n$conn_id = ftp_connect($ftp_server);\n\n// Login with username and password\n$login_result = ftp_login($conn_id, $ftp_user_name, $ftp_user_pass);\n\nif ((!$conn_id) || (!$login_result)) {\n    die("FTP connection has failed!");\n}\n\n// Download file\n$local_file = "Final.pdf";  // Local filename to save as\nif (ftp_get($conn_id, $local_file, $remote_file, FTP_BINARY)) {\n    echo "Successfully written to $local_file\n";\n    // You can now process or move this file as needed\n} else {\n    echo "There was a problem while downloading the file.\n";\n}\n\n// Close the connection\nftp_close($conn_id);\n?>