function isBot() {\n    $userAgent = $_SERVER['HTTP_USER_AGENT'];\n\n    // List of some common search engine bots\n    $bots = [\n        'Googlebot',\n        'Bingbot',\n        'YandexBot',\n        'Slurp', // Yahoo! bot\n        'DuckDuckGo-Favicons-Bot',\n        'Baiduspider',\n        // Add more bots as necessary\n    ];\n\n    foreach ($bots as $bot) {\n        if (stripos($userAgent, $bot) !== false) {\n            return true;\n        }\n    }\n\n    return false;\n}\n\nif (isBot()) {\n    echo "This request is likely from a search engine bot.";\n} else {\n    echo "This request is not from a search engine bot.";\n}