// application/config/routes.php\n\nRoute::set('default', '(<controller>(/<action>(/<id>)))')\n     ->defaults([\n         'directory'  => '',\n         'controller' => 'welcome',\n         'action'     => 'index'\n     ])\n     ->filter(function($route, $params, $request) {\n         // Convert hyphenated controller and action names to camelCase\n         if (isset($params['controller'])) {\n             $params['controller'] = strtolower(preg_replace('/(?<!^)[A-Z]/', '-$0', $params['controller']));\n             $parts = explode('-', $params['controller']);\n             foreach ($parts as &$part) {\n                 $part = ucfirst(strtolower($part));\n             }\n             $params['controller'] = implode('', $parts);\n             \n             if (strpos($params['action'], '-') !== false) {\n                 $actionParts = explode('-', $params['action']);\n                 foreach ($actionParts as &$actionPart) {\n                     $actionPart = lcfirst(strtolower($actionPart));\n                 }\n                 $params['action'] = implode('', $actionParts);\n             }\n         }\n\n         return $params;\n     });