<?php\n$serverName = "your_server"; // Your server name\n$connectionOptions = array(\n    "Database" => "your_database", // Your database name\n    "Uid" => "your_username",      // Your username\n    "PWD" => "<PASSWORD>"           // Your password\n);\n\n// Establishes the connection\n$conn = sqlsrv_connect($serverName, $connectionOptions);\nif ($conn === false) {\n    die(print_r(sqlsrv_errors(), true));\n}\n\n$sql = "\nDECLARE @id bigint; \nSET @id = (SELECT ISNULL(MAX(application_track_id), 0) + 1 FROM application_track_data); \nINSERT INTO application_track_data (application_track_id,user_id, action_key, action, ip_address, session_id, application) \nVALUES (@id,1,'584','login','192.168.37.60','05sn3618p61dvmml6pkefuteg2','akamata');\n";\n\n$result = sqlsrv_query($conn, $sql);\nif ($result === false) {\n    die(print_r(sqlsrv_errors(), true));\n} else {\n    echo "Success";\n}\n\n// Free statement and connection resources\nsqlsrv_free_stmt($result);\nsqlsrv_close($conn);\n?>