<?php\n// Load an image from file\n$image = imagecreatefromjpeg('input.jpg');\n\nif ($image !== false) {\n    $width  = imagesx($image);\n    $height = imagesy($image);\n\n    // Create a mask\n    $mask = imagecreatetruecolor($width, $height);\n    $transparent = imagecolorallocatealpha($mask, 0, 0, 0, 127);\n    imagecolortransparent($mask, $transparent);\n    imagealphablending($mask, false);\n    imagesavealpha($mask, true);\n\n    // Fill the mask with transparency\n    imagefill($mask, 0, 0, $transparent);\n\n    // Draw a circle onto the mask\n    $radius = min($width, $height) / 2;\n    $centerX = $width / 2;\n    $centerY = $height / 2;\n    $white = imagecolorallocatealpha($mask, 255, 255, 255, 0);\n    imagefilledellipse($mask, $centerX, $centerY, $radius * 2, $radius * 2, $white);\n\n    // Apply the mask to the original image\n    imagealphablending($image, true);\n    imagesavealpha($image, true);\n    imagecopymerge($image, $mask, 0, 0, 0, 0, $width, $height, 100);\n\n    // Save or display the resulting image\n    imagepng($image, 'output.png');\n    imagedestroy($image);\n    imagedestroy($mask);\n}\n?>