$sscount = $_POST['imgcount'];\necho $sscount; //to test the variable\n\nif ($sscount > 0) {\n    for ($i = 1; $i <= $sscount; $i++) {\n        $fileType = $_FILES["image$i"]["type"];\n        $fileSize = $_FILES["image$i"]["size"];\n        $filePath = $_FILES["image$i"]["tmp_name"];\n\n        // Check file size\n        if ($fileSize > 500000) {\n            $errormsg .= "Error: Image $i must be less than 500 kb.<br />";\n            continue;\n        }\n\n        // Verify image type using getimagesize\n        $imageInfo = getimagesize($filePath);\n        if (!$imageInfo || !in_array($imageInfo['mime'], ['image/gif', 'image/jpeg', 'image/png'])) {\n            $errormsg .= "Error: Image $i is not a valid JPEG, GIF, or PNG file.<br />";\n            continue;\n        }\n\n        // Move the uploaded file or process it here...\n    }\n}