function runDatabaseUpgrade($newVersion) {\n    $upgradeFile = __DIR__ . "/db_upgrades/{$newVersion}.sql";\n    if (file_exists($upgradeFile)) {\n        $queries = file_get_contents($upgradeFile);\n        foreach (explode(';', $queries) as $query) {\n            if (!empty(trim($query))) {\n                mysqli_query($connection, trim($query));\n            }\n        }\n    }\n}