<?php\n\nfunction replaceTagsWithIncludes($input) {\n    // Define the regular expression pattern to match {UPPERCASE_ALPHANUMERIC_TAGS}\n    $pattern = '/\{([A-Z0-9\-]+)\}/';\n\n    return preg_replace_callback(\n        $pattern,\n        function ($matches) {\n            // Construct the file name from the matched tag\n            $filename = __DIR__ . '/' . $matches[1] . '.php';\n            \n            // Check if the file exists before trying to get its contents\n            if (file_exists($filename)) {\n                return file_get_contents($filename);\n            } else {\n                // Optionally log or handle missing files\n                error_log("File not found: " . $filename);\n                return '';\n            }\n        },\n        $input\n    );\n}\n\n// Example usage:\n$string = "This is a test string with {TEST} and another tag {CONTACT-FORM}.";\n$result = replaceTagsWithIncludes($string);\n\necho $result;\n\n?>