function myAutoloader($class) {\n    // Define the base directory for the class files\n    $baseDir = __DIR__ . '/classes/';\n\n    // Normalize the namespace path separators\n    $file = str_replace('\\', DIRECTORY_SEPARATOR, $class) . '.class.php';\n\n    // Check both lowercase and uppercase filenames\n    $filesToCheck = [\n        strtolower($baseDir . $file),\n        strtoupper($baseDir . $file)\n    ];\n\n    foreach ($filesToCheck as $filePath) {\n        if (file_exists($filePath)) {\n            require_once $filePath;\n            return;\n        }\n    }\n\n    // Optionally, you can throw an exception if the class is not found\n    throw new Exception("Class {$class} not found in {$baseDir}");\n}\n\nspl_autoload_register('myAutoloader');