function getFirstSentence($text) {\n    // Use a regular expression to match the first sentence\n    preg_match('/^([^.!?]*[.!?])/', $text, $matches);\n    // Return the matched sentence or an empty string if nothing is found\n    return isset($matches[1]) ? trim($matches[1]) : '';\n}\n\n// Example usage\n$description = "We prefer questions that can be answered, not just discussed. Provide details. Write clearly and simply.";\necho getFirstSentence($description);