<?php\nheader("Content-Type: application/json");\nif (!$isLoggedIn) {\n    http_response_code(401);\n    echo json_encode(["errorMessage" => "User is not logged in"]);\n    exit;\n}\nif (!$hasPermission) {\n    http_response_code(403);\n    echo json_encode(["errorMessage" => "User does not have permission"]);\n    exit;\n}\nif (!isValidData($data)) {\n    http_response_code(400);\n    echo json_encode(["errorMessage" => "Invalid data provided"]);\n    exit;\n}\n\n// Proceed with the operation\necho json_encode(["successMessage" => "Operation successful", "data" => $result]);\n?>