<?php\n\nfunction encodeURL($url) {\n    // Generate a unique hash based on the URL\n    $hash = base64_encode(hash_hmac('sha256', $url, 'your_secret_key_here', true));\n    \n    // Convert Base64 to Base62 by removing non-alphanumeric characters and padding\n    $base62 = preg_replace('/[^0-9a-zA-Z]/', '', strtr($hash, '+/', '-_'));\n    \n    return substr($base62, 0, 10); // Take the first 10 characters for brevity\n}\n\nfunction encodeEmail($email) {\n    // Generate a unique hash based on the email\n    $hash = base64_encode(hash_hmac('sha256', $email, 'your_secret_key_here', true));\n    \n    // Convert Base64 to Base62 by removing non-alphanumeric characters and padding\n    $base62 = preg_replace('/[^0-9a-zA-Z]/', '', strtr($hash, '+/', '-_'));\n    \n    return substr($base62, 0, 15); // Take the first 15 characters for more uniqueness given similar emails\n}\n\n// Example usage\n$urlEncoded = encodeURL('https://stackoverflow.com/1/');\n$emailEncoded = encodeEmail('example@example.com');\n\necho 'Encoded URL: ' . $urlEncoded . PHP_EOL;\necho 'Encoded Email: ' . $emailEncoded . PHP_EOL;\n\n?>