<?php\n\n// Assuming $youtubeVideoIds is an array of video IDs fetched from YouTube\n$youtubeVideoIds = [];\nforeach ($feed as $entry) {\n    $youtubeVideoIds[] = $entry->getVideoId();\n}\n\n// Fetch existing Video IDs from your database\n$dbVideoIds = [];\n$query = "SELECT yt_id FROM videos";\n$result = mysqli_query($conn, $query);\nwhile ($row = mysqli_fetch_assoc($result)) {\n    $dbVideoIds[] = $row['yt_id'];\n}\n\n// Find new video IDs to add\n$newVideosToAdd = array_diff($youtubeVideoIds, $dbVideoIds);\n\n// Insert new video IDs into the database\nif (!empty($newVideosToAdd)) {\n    foreach ($newVideosToAdd as $videoId) {\n        $stmt = mysqli_prepare($conn, "INSERT INTO videos (yt_id) VALUES (?)");\n        mysqli_stmt_bind_param($stmt, "s", $videoId);\n        mysqli_stmt_execute($stmt);\n        mysqli_stmt_close($stmt);\n    }\n}\n\n// Note: No action is taken for deleted YouTube videos as per your requirement\n\n?>