<?php\n\n$urls = []; // Populate this array with your URLs\n\n// Initialize a cURL multi handle\n$mh = curl_multi_init();\n\n// Array of cURL handler resources\n$conn = [];\n\nforeach ($urls as $i => $url) {\n    $conn[$i] = curl_init();\n    \n    // Set options for each cURL handle\n    curl_setopt($conn[$i], CURLOPT_URL, $url);\n    curl_setopt($conn[$i], CURLOPT_POST, 0);\n    curl_setopt($conn[$i], CURLOPT_COOKIEFILE, 'files/cookie.txt');\n    curl_setopt($conn[$i], CURLOPT_FOLLOWLOCATION, 0);\n    curl_setopt($conn[$i], CURLOPT_RETURNTRANSFER, true);\n\n    // Add handle to the multi cURL sessions\n    curl_multi_add_handle($mh, $conn[$i]);\n}\n\n$running = null;\n\n// Execute all handles until there are none left\ndo {\n    curl_multi_exec($mh, $running);\n} while ($running > 0);\n\n$responseData = [];\nforeach ($urls as $i => $url) {\n    // Get the content that was retrieved using cURL\n    $responseData[$i] = curl_multi_getcontent($conn[$i]);\n\n    // Close each handle\n    curl_multi_remove_handle($mh, $conn[$i]);\n}\n\n// Close the multi handle\ncurl_multi_close($mh);\n\nprint_r($responseData); // Display all responses\n\n?>