function ensureWww($url) {\n    // Remove any existing protocol part for consistency\n    $parsedUrl = parse_url($url);\n    $host = isset($parsedUrl['host']) ? $parsedUrl['host'] : '';\n    $path = isset($parsedUrl['path']) ? $parsedUrl['path'] : '';\n    $query = isset($parsedUrl['query']) ? '?' . $parsedUrl['query'] : '';\n    $fragment = isset($parsedUrl['fragment']) ? '#' . $parsedUrl['fragment'] : '';\n\n    // Check if 'www' is part of the host\n    if (strpos($host, 'www.') !== 0) {\n        $host = 'www.' . $host;\n    }\n\n    // Reconstruct URL with www in host\n    return "http://{$host}{$path}{$query}{$fragment}";\n}\n\n// Example usage\n$url1 = "http://imdb.com/whatever";\n$url2 = "http://www.imdb.com/something";\n\necho ensureWww($url1); // Outputs: http://www.imdb.com/whatever\necho ensureWww($url2); // Outputs: http://www.imdb.com/something