function updateActionLog($ticket_num, $new_actionlog) {\n    $data = array(\n        'actionlog' => $new_actionlog\n    );\n\n    $this->db->where('ticketnum', $ticket_num);\n    $this->db->update('tickets', $data);\n}\n\nfunction actionLogPull($requestedNum=NULL) {\n    $query = $this->db->get_where('tickets', array('ticketnum' => $requestedNum));\n    return ($query->num_rows() > 0) ? $query->row()->actionlog : '';\n}