<?php\n// User credentials for ServerB\n$username = $_POST['username'];\n$password = $_POST['password'];\n\n// WebDAV URL where to upload the file\n$webdav_url = "http://serverb.com/path/to/icalfile.ics";\n\n// iCalendar content (example)\n$ical_content = <<<EOD\nBEGIN:VCALENDAR\nVERSION:2.0\nPRODID:-//Example Corp.//CalDAV Server//EN\nCALSCALE:GREGORIAN\nMETHOD:PUBLISH\nBEGIN:VEVENT\nUID:event1@example.com\nSUMMARY:Test Event\nDTSTART:20231201T150000Z\nDTEND:20231201T160000Z\nEND:VEVENT\nEND:VCALENDAR\nEOD;\n\n// Initialize cURL session\n$ch = curl_init();\n\n// Set options for the POST request to upload files via WebDAV (PUT)\ncurl_setopt($ch, CURLOPT_URL, $webdav_url);\ncurl_setopt($ch, CURLOPT_USERPWD, "$username:$password");\ncurl_setopt($ch, CURLOPT_CUSTOMREQUEST, "PUT"); // WebDAV PUT method\ncurl_setopt($ch, CURLOPT_POSTFIELDS, $ical_content); // Content to upload\n\n// Execute cURL\n$response = curl_exec($ch);\n\n// Check for errors\nif(curl_errno($ch)) {\n    echo 'cURL error: ' . curl_error($ch);\n} else {\n    http_response_code(200);\n    echo "File uploaded successfully.";\n}\n\n// Close cURL session\ncurl_close($ch);\n?>